INSERT INTO `wp_contact_form_7` VALUES (1, 'Свяжитесь с нами', '<p>Ваше имя (обязательно)<br />\r\n    [text* your-name] </p>\r\n\r\n<p>Ваш E-Mail (обязательно)<br />\r\n    [email* your-email] </p>\r\n\r\n<p>Тема<br />\r\n    [text your-subject] </p>\r\n\r\n<p>Сообщение<br />\r\n    [textarea your-message] </p>\r\n\r\n<p>[submit "Отправить"]</p>', 'a:7:{s:7:"subject";s:14:"[your-subject]";s:6:"sender";s:26:"[your-name] <[your-email]>";s:4:"body";s:247:"От: [your-name] <[your-email]>\r\nТема: [your-subject]\r\n\r\nТело сообщения:\r\n[your-message]\r\n\r\n--\r\nЭто сообщение отправлено посредством контактной формы на IT-Consalt http://test1.ru";s:9:"recipient";s:24:"founder@itconsalt.com.ua";s:18:"additional_headers";s:9:"From_Site";s:11:"attachments";s:0:"";s:8:"use_html";s:0:"";}', 'a:8:{s:6:"active";s:0:"";s:7:"subject";s:14:"[your-subject]";s:6:"sender";s:26:"[your-name] <[your-email]>";s:4:"body";s:185:"Тело сообщения:\r\n[your-message]\r\n\r\n--\r\nЭто сообщение отправлено посредством контактной формы на IT-Consalt http://test1.ru";s:9:"recipient";s:12:"[your-email]";s:18:"additional_headers";s:0:"";s:11:"attachments";s:0:"";s:8:"use_html";s:0:"";}', 'a:13:{s:12:"mail_sent_ok";s:89:"Ваше сообщение было отправлено успешно. Спасибо.";s:12:"mail_sent_ng";s:155:"Ошибка отправки сообщения. Попытайтесь позже или обратитесь к администратору сайта.";s:17:"akismet_says_spam";s:155:"Ошибка отправки сообщения. Попытайтесь позже или обратитесь к администратору сайта.";s:16:"validation_error";s:103:"Ошибка заполнения. Заполните все поля и отправьте снова.";s:12:"accept_terms";s:82:"Пожалуйста, примите условия для продолжения.";s:13:"invalid_email";s:43:"Некорректный e-mail адрес.";s:16:"invalid_required";s:75:"Пожалуйста, заполните обязательные поля.";s:17:"captcha_not_match";s:51:"Код введен Вами неправильно";s:13:"upload_failed";s:48:"Не удалось загрузить файл.";s:24:"upload_file_type_invalid";s:49:"Этот тип файле не разрешен.";s:21:"upload_file_too_large";s:48:"Этот файл слишком большой.";s:23:"upload_failed_php_error";s:79:"Отправка файла не удалась. Возникла ошибка.";s:23:"quiz_answer_not_correct";s:50:"Вы ввели некорретный ответ.";}', '');
INSERT INTO `wp_px_plugins` VALUES (1, 'jQuery Gallery Viewer', 'jqGalView', '\r\n#px *{\r\n	margin:0;\r\n	padding:0;\r\n	background:none;\r\n	border:none;\r\n	list-style:none;\r\n}\r\n#px .gvContainer{\r\n	width:600px;\r\n	height:356px;\r\n	background:url({PXPATH}/img/bg_main.jpg);\r\n	overflow:hidden;\r\n	position:relative;\r\n}\r\n#px .gvContainer .gvHolder{\r\n	position:absolute;\r\n	top:0;\r\n	left:0;\r\n	margin-left:0;\r\n	margin-top:0;\r\n}\r\n#px .gvHeader{\r\n	width:600px;\r\n	background:url({PXPATH}/img/bg_top.jpg);\r\n	color:#fff;\r\n	font:bold 11px Geneva, Arial, Helvetica, sans-serif;\r\n	height:44px;\r\n	position:relative;\r\n}\r\n#px .gvHeader span{\r\n	color:#fff;\r\n	font:bold 20px/44px Verdana, Arial, Helvetica, sans-serif;\r\n	padding-left:35px;\r\n}\r\n#px .gvFooter{\r\n	width:600px;\r\n	background: url({PXPATH}/img/bg_bot.jpg);\r\n	color:#fff;\r\n	font:bold 11px Geneva, Arial, Helvetica, sans-serif;\r\n	height:20px;\r\n	text-align:center;\r\n}\r\n#px .gvFooter .gvLinks{\r\n	padding:0px auto;\r\n	padding-top:3px;\r\n}\r\n#px .gvFooter a{\r\n	padding:0 10px;\r\n	width:15px;\r\n	background:#CCCCCC;\r\n	color:#666666;\r\n}\r\n#px .gvFooter a:hover{\r\n	background:#fff;\r\n	color:#666666;\r\n}\r\n#px 	.gvHeader strong{\r\n	padding-left:10px;\r\n	font:bold 11px/20px Geneva, Arial, Helvetica, sans-serif;\r\n}\r\n#px .gvHeader a.gvFullSizeText{\r\n	position:absolute;\r\n	right:0;\r\n	top:0;\r\n	display:none;\r\n	font:bold 10px/20px Verdana, Arial, Helvetica, sans-serif;\r\n	color:#fff;\r\n	width:120px;\r\n	text-align:right;\r\n	padding-right:5px;\r\n}\r\n#px .gvContainer .gvItem{\r\n	width:116px;\r\n	text-align:center;\r\n	vertical-align:middle;\r\n	height:85px;\r\n	position:relative;\r\n	float:left;\r\n	border:1px solid #6E8D2B;\r\n	margin:1px;\r\n	overflow:hidden;\r\n	background:#fff;\r\n}\r\n/*stupid ie6 fix*/\r\n* html #px .gvContainer .gvItem {\r\n	margin-left: 0;\r\n	margin-right: 2px;\r\n}\r\n#px .gvContainer .gvItem .gvOpen{\r\n	position:absolute;\r\n	top:0;\r\n	left:0;\r\n	height:15px;\r\n	width:116px;\r\n	font:bold 8px/15px Verdana, Arial, Helvetica, sans-serif;\r\n	color:#6E8D2B;\r\n	background:#C1FD3E ;\r\n	border-bottom:1px solid #6E8D2B;\r\n	text-align:right;\r\n	text-transform:uppercase;\r\n}\r\n#px .gvContainer .gvItem img{\r\n	position:absolute;\r\n	top:0;\r\n	left:0;\r\n	padding:0;\r\n	margin:0;\r\n	margin-left:0;\r\n	margin-top:0;border:none;\r\n	cursor:pointer;\r\n}\r\n#px .gvImgContainer{\r\n	position:absolute;\r\n	top:0;\r\n	left:0;\r\n	width:600px;\r\n	height:356px;\r\n	display:none;\r\n	overflow:hidden;\r\n	background:url({PXPATH}/img/bg_main.jpg);\r\n}\r\n#px .gvImgContainer .gvDescText{\r\n	width:400px;\r\n	position:absolute;\r\n	bottom:0;\r\n	left:50%;\r\n	margin-left:-200px;\r\n	font:11px/15px Verdana, Arial, Helvetica, sans-serif;\r\n	color:#6E8D2B;\r\n	background:#C1FD3E ;\r\n	border:1px solid #6E8D2B;\r\n	border-bottom:none;\r\n	display:none;	\r\n	padding:3px;	\r\n}\r\n#px .gvImgContainer img{\r\n	border:none;\r\n	padding:0;\r\n	margin:0;\r\n	cursor:pointer;\r\n	display:none;\r\n}\r\n#px .gvLoader{\r\n	background:url({PXPATH}/img/loadingAnimation.gif);\r\n	width:42px;\r\n	height:42px;\r\n	position:absolute;\r\n	left:50%;\r\n	top:50%;\r\n	margin-left:-21px;\r\n	margin-top:-21px;\r\n	display:none;\r\n}\r\n#px .gvLoaderMini{\r\n	background:url({PXPATH}/img/ajax-loader.gif);\r\n	width:42px;\r\n	height:42px;\r\n	position:absolute;\r\n	left:50%;\r\n	top:50%;\r\n	margin-left:-21px;\r\n	margin-top:-21px;\r\n}\r\n		', 'http://benjaminsterling.com/jquery-jqgalview-photo-gallery/', '{"parameters":[{"param":"openTxt","desc":"The text you want to have shown when you hover over a thumbnail"},{"param":"backTxt","desc":"The text that gets append to the full images title attribute to give a hint for the user to click to return to the thumbnails"},  {"param":"goFullSizeTxt","desc":"The text that gets appended after the alt text when the full sized image is being view and will allow the user to view the resized full image in a new browser window or in the modal box"},  {"param":"title","desc":"Set to null by default and will allow you to add a title to gallery if not already set in a title tag of the parent element of the thumbnail group"}]}', 'jQuery'); 
INSERT INTO `wp_px_plugins` VALUES (2, 'jQuery Gallery Viewer II', 'jqGalViewII', '\r\n#px .gvIIContainer{\r\n	width:629px;\r\n	position:relative;\r\n	background:#000;\r\n	padding:10px 0 10px 10px;\r\n	border:10px solid #fff;\r\n}\r\n#px .gvIIContainer .gvIIImgContainer{\r\n	width:619px;\r\n	height:385px;\r\n	position:relative;\r\n	overflow:hidden;\r\n	margin-bottom:10px;\r\n}\r\n#px .gvIIContainer .gvIIImgContainer .gvIILoader{\r\n	background: url({PXPATH}/ajax-loader.gif) no-repeat center center;\r\n	width:619px;\r\n	height:385px;\r\n	display:none;\r\n}\r\n#px .gvIIContainer .gvIIHolder{\r\n	position:relative;\r\n	height:207px;\r\n	width:619px;\r\n	overflow:auto;\r\n}\r\n#px .gvIIContainer .gvIIHolder .gvIIArrow{}\r\n#px .gvIIContainer .gvIIHolder .gvIIItem{\r\n	float:left;\r\n	height:55px;\r\n	width:72px;\r\n	border:5px solid #fff;\r\n	margin:2px;\r\n	position:relative;\r\n	overflow:hidden;\r\n}\r\n#px .gvIIContainer .gvIIHolder .gvIIItem .gvIIFlash{\r\n	background:#fff;\r\n	position:absolute;\r\n	top:0;\r\n	left:0;\r\n	height:55px;\r\n	width:72px;\r\n	cursor:pointer;\r\n}\r\n#px .gvIIContainer .gvIIHolder .gvIIItem img{\r\n	position:absolute;\r\n	top:0;\r\n	left:0;\r\n	padding:0;\r\n	margin:0;\r\n	margin-left:0;\r\n	margin-top:0;\r\n	border:none;\r\n	cursor:pointer;\r\n}\r\n		', 'http://benjaminsterling.com/?p=14', '', 'jQuery'); 
INSERT INTO `wp_px_plugins` VALUES (3, 'jQuery jqGalScroll Plugin', 'jqGalScroll', '\r\n#px *{\r\n	margin:0;\r\n	padding:0;\r\n	background:none;\r\n	border:none;\r\n}\r\n#px .jqGSContainer{\r\n	position:relative;\r\n	width:502px;\r\n	clear:both;\r\n	padding-bottom:10px;\r\n}\r\n#px .jqGSContainer .jqGSImgContainer{\r\n}\r\n#px .jqGSContainer .jqGSImgContainer ul{\r\n	padding:0;\r\n	margin:0;\r\n	position:relative;\r\n	list-style:none;\r\n}\r\n#px .jqGSContainer .jqGSImgContainer ul li{\r\n	padding:0;\r\n	margin:0;\r\n	position:relative;\r\n	margin-top:0;\r\n	margin-bottom:0px;\r\n	float:left;\r\n}\r\n#px .jqGSContainer .jqGSImgContainer ul li .jqGSLoader{\r\n	width:100%;\r\n	height:100%;\r\n	position:absolute;\r\n}\r\n#px .jqGSContainer .jqGSImgContainer ul li img{\r\n	border:1px solid #fff;\r\n}\r\n#px .jqGSContainer .jqGSImgContainer ul li .jqGSTitle{\r\n	background:#000;\r\n	position:absolute;\r\n	right:0px;\r\n	top:0px;\r\n	padding:3px;\r\n	color:#fff;\r\n	width:300px;\r\n}\r\n#px .jqGSContainer .jqGSPagination{\r\n	position:relative;\r\n	width:100%;\r\n	height:30px;\r\n	top:5px;\r\n	padding: 5px 0;\r\n}\r\n#px .jqGSContainer .jqGSPagination ul{\r\n	padding:0;\r\n	margin:0;\r\n	list-style:none;\r\n	position:relative;\r\n	float:right;\r\n}\r\n#px .jqGSContainer .jqGSPagination ul li{\r\n	padding:0;\r\n	margin-right:5px;\r\n	float:left;\r\n	padding-right:1px;\r\n	text-align:center;\r\n	padding-bottom:1px;\r\n}\r\n#px .jqGSContainer .jqGSPagination ul li a{\r\n	padding:2px 0px;\r\n	background:#000;\r\n	border:1px solid #fff;\r\n	color: #fff;\r\n	text-decoration:none;\r\n	display:block;\r\n	width:20px;\r\n	font:10px Verdana, Arial, Helvetica, sans-serif;\r\n}\r\n#px .jqGSContainer .jqGSPagination ul li a.selected{\r\n	background:#fff;\r\n	color:#f03;\r\n	border: 1px solid #eaeaea;\r\n}\r\n#px .jqGSContainer .jqGSPagination ul li a:hover{\r\n	background:#fff;\r\n	color:#000;\r\n	border:1px solid #fff;\r\n}\r\n#px .jqGSImgContainer{\r\n	border:1px solid #000;\r\n}\r\n		', 'http://benjaminsterling.com/jquery-jqgalscroll-photo-gallery/', '{"parameters":[{"param":"speed","desc":"fast, slow, 1000, ext.."},{"param":"height","desc":"the default height of your wrapper"},{"param":"width","desc":"the default width of your wrapper"},{"param":"titleOpacity","desc":"the opacity of your title bar (if present)"},{"param":"direction","desc":"The direction of the scroll, values vertical, horizontal, or diagonal"}]}', 'jQuery'); 
INSERT INTO `wp_px_plugins` VALUES (4, 'cycle', 'cycle', '\r\n.pics {\r\n	height:232px;\r\n	margin:0pt;\r\n	overflow:hidden;\r\n	padding:0pt;\r\n	width:232px;\r\n}\r\n.pics img {\r\n	background-color:#EEEEEE;\r\n	border:1px solid #CCCCCC;\r\n	height:200px;\r\n	left:0pt;\r\n	padding:15px;\r\n	top:0pt;\r\n	width:200px;\r\n}\r\n\r\n#px{\r\n	position:relative;\r\n	height:auto;\r\n	list-style:none;\r\n	padding:0;\r\n	margin:0;\r\n	border:none;\r\n}\r\n\r\n#px a{\r\n	border:none;\r\n	padding:0;\r\n	margin:0;\r\n}\r\n\r\n#px li{\r\n	border:none;\r\n	padding:0;\r\n	margin:0;\r\n	height:auto;\r\n	border:none;\r\n}\r\n		', 'http://www.malsup.com/jquery/cycle/', '{"parameters":[{"param":"fx","desc":"one of: fade, shuffle, zoom, slideX, slideY, scrollUp/Down/Left/Right"},{"param":"timeout","desc":"milliseconds between slide transitions (0 to disable auto advance) "},{"param":"speed","desc":"speed of the transition (any valid fx speed value) "},{"param":"speedIn","desc":"speed of the `in` transition "},{"param":"speedOut","desc":"peed of the `out` transition"},{"param":"easing","desc":"easing method for both in and out transitions "},{"param":"easeIn","desc":"easing for `in` transition "},{"param":"easeOut","desc":"easing for `out` transition "},{"param":"shuffle","desc":"coords for shuffle animation, ex: { top:15, left: 200 } "},{"param":"animIn","desc":"properties that define how the slide animates in "},{"param":"animOut","desc":"properties that define how the slide animates out "},{"param":"height","desc":"container height "},{"param":"sync","desc":"true if in/out transitions should occur simultaneously "},{"param":"random","desc":"true for random, false for sequence (not applicable to shuffle fx)"},{"param":"fit","desc":"force slides to fit container "},{"param":"pause","desc":"true to enable `pause on hover` "},{"param":"autostop","desc":"true to end slideshow after X transitions (where X == slide count) "},{"param":"delay","desc":"additional delay (in ms) for first transition (hint: can be negative)}; "}]}', 'jQuery'); 
INSERT INTO `wp_px_plugins` VALUES (5, 'Thickbox', 'thickbox', '\r\n/*/* ----------------------------------------------------------------------------------------------------------------*/\r\n/* ---------->>> global settings needed for thickbox <<<-----------------------------------------------------------*/\r\n/* ----------------------------------------------------------------------------------------------------------------*/\r\n*{padding: 0; margin: 0;}\r\n/* ----------------------------------------------------------------------------------------------------------------*/\r\n/* ---------->>> thickbox specific link and font settings <<<------------------------------------------------------*/\r\n/* ----------------------------------------------------------------------------------------------------------------*/\r\n#TB_window {\r\n  font: 12px Arial, Helvetica, sans-serif;\r\n  color: #333333;\r\n}\r\n#TB_secondLine {\r\n  font: 10px Arial, Helvetica, sans-serif;\r\n  color:#666666;\r\n}\r\n#TB_window a:link {color: #666666;}\r\n#TB_window a:visited {color: #666666;}\r\n#TB_window a:hover {color: #000;}\r\n#TB_window a:active {color: #666666;}\r\n#TB_window a:focus{color: #666666;}\r\n/* ----------------------------------------------------------------------------------------------------------------*/\r\n/* ---------->>> thickbox settings <<<-----------------------------------------------------------------------------*/\r\n/* ----------------------------------------------------------------------------------------------------------------*/\r\n#TB_overlay {\r\n  position: fixed;\r\n  z-index:100;\r\n  top: 0px;\r\n  left: 0px;\r\n  height:100%;\r\n  width:100%;\r\n}\r\n.TB_overlayMacFFBGHack {background: url({PXPATH}/macFFBgHack.png) repeat;}\r\n.TB_overlayBG {\r\n  background-color:#000;\r\n  filter:alpha(opacity=75);\r\n  -moz-opacity: 0.75;\r\n  opacity: 0.75;\r\n}\r\n* html #TB_overlay { /* ie6 hack */\r\n     position: absolute;\r\n     height: expression(document.body.scrollHeight > document.body.offsetHeight ? document.body.scrollHeight : document.body.offsetHeight + \\\'px\\\');\r\n}\r\n#TB_window {\r\n  position: fixed;\r\n  background: #ffffff;\r\n  z-index: 102;\r\n  color:#000000;\r\n  display:none;\r\n  border: 4px solid #525252;\r\n  text-align:left;\r\n  top:50%;\r\n  left:50%;\r\n}\r\n* html #TB_window { /* ie6 hack */\r\nposition: absolute;\r\nmargin-top: expression(0 - parseInt(this.offsetHeight / 2) + (TBWindowMargin = document.documentElement && document.documentElement.scrollTop || document.body.scrollTop) + \\\'px\\\');\r\n}\r\n#TB_window img#TB_Image {\r\n  display:block;\r\n  margin: 15px 0 0 15px;\r\n  border-right: 1px solid #ccc;\r\n  border-bottom: 1px solid #ccc;\r\n  border-top: 1px solid #666;\r\n  border-left: 1px solid #666;\r\n}\r\n#TB_caption{\r\n  height:25px;\r\n  padding:7px 30px 10px 25px;\r\n  float:left;\r\n}\r\n#TB_closeWindow{\r\n  height:25px;\r\n  padding:11px 25px 10px 0;\r\n  float:right;\r\n}\r\n#TB_closeAjaxWindow{\r\n  padding:7px 10px 5px 0;\r\n  margin-bottom:1px;\r\n  text-align:right;\r\n  float:right;\r\n}\r\n#TB_ajaxWindowTitle{\r\n  float:left;\r\n  padding:7px 0 5px 10px;\r\n  margin-bottom:1px;\r\n}\r\n#TB_title{\r\n  background-color:#e8e8e8;\r\n  height:27px;\r\n}\r\n#TB_ajaxContent{\r\n  clear:both;\r\n  padding:2px 15px 15px 15px;\r\n  overflow:auto;\r\n  text-align:left;\r\n  line-height:1.4em;\r\n}\r\n#TB_ajaxContent.TB_modal{\r\n  padding:15px;\r\n}\r\n#TB_ajaxContent p{\r\n  padding:5px 0px 5px 0px;\r\n}\r\n#TB_load{\r\n  position: fixed;\r\n  display:none;\r\n  height:13px;\r\n  width:208px;\r\n  z-index:103;\r\n  top: 50%;\r\n  left: 50%;\r\n  background:url({PXPATH}/loadingAnimation.gif) no-repeat center center;\r\n  margin: -6px 0 0 -104px; /* -height/2 0 0 -width/2 */\r\n}\r\n* html #TB_load { /* ie6 hack */\r\nposition: absolute;\r\nmargin-top: expression(0 - parseInt(this.offsetHeight / 2) + (TBWindowMargin = document.documentElement && document.documentElement.scrollTop || document.body.scrollTop) + \\\'px\\\');\r\n}\r\n#TB_HideSelect{\r\n  z-index:99;\r\n  position:fixed;\r\n  top: 0;\r\n  left: 0;\r\n  background-color:#fff;\r\n  border:none;\r\n  filter:alpha(opacity=0);\r\n  -moz-opacity: 0;\r\n  opacity: 0;\r\n  height:100%;\r\n  width:100%;\r\n}\r\n* html #TB_HideSelect { /* ie6 hack */\r\n     position: absolute;\r\n     height: expression(document.body.scrollHeight > document.body.offsetHeight ? document.body.scrollHeight : document.body.offsetHeight + \\\'px\\\');\r\n}\r\n#TB_iframeContent{\r\n  clear:both;\r\n  border:none;\r\n  margin-bottom:-1px;\r\n  margin-top:1px;\r\n  _margin-bottom:1px;\r\n}\r\n		', 'http://jquery.com/demo/thickbox/', '', 'jQuery'); 
INSERT INTO `wp_px_plugins` VALUES (6, 'Light Box', 'lightBox', '\r\n#jquery-overlay {\r\n	position: absolute;\r\n	top: 0;\r\n	left: 0;\r\n	z-index: 90;\r\n	width: 100%;\r\n	height: 500px;\r\n}\r\n#jquery-lightbox {\r\n	position: absolute;\r\n	top: 0;\r\n	left: 0;\r\n	width: 100%;\r\n	z-index: 100;\r\n	text-align: center;\r\n	line-height: 0;\r\n}\r\n#jquery-lightbox a img { border: none; }\r\n#lightbox-container-image-box {\r\n	position: relative;\r\n	background-color: #fff;\r\n	width: 250px;\r\n	height: 250px;\r\n	margin: 0 auto;\r\n}\r\n#lightbox-container-image { padding: 10px; }\r\n#lightbox-loading {\r\n	position: absolute;\r\n	top: 40%;\r\n	left: 0%;\r\n	height: 25%;\r\n	width: 100%;\r\n	text-align: center;\r\n	line-height: 0;\r\n}\r\n#lightbox-nav {\r\n	position: absolute;\r\n	top: 0;\r\n	left: 0;\r\n	height: 100%;\r\n	width: 100%;\r\n	z-index: 10;\r\n}\r\n#lightbox-container-image-box > #lightbox-nav { left: 0; }\r\n#lightbox-nav a { outline: none;}\r\n#lightbox-nav-btnPrev, #lightbox-nav-btnNext {\r\n	width: 49%;\r\n	height: 100%;\r\n	zoom: 1;\r\n	display: block;\r\n	display:-moz-inline-box;\r\n	display:inline-block;\r\n	vertical-align:bottom;\r\n}\r\n#lightbox-nav-btnPrev { \r\n	left: 0; \r\n	float: left;\r\n	height:63px;\r\n	width:72px;\r\n	background: transparent url({PXPATH}/lightbox-btn-prev.gif)  left 15% no-repeat;\r\n}\r\n#lightbox-nav-btnNext { \r\n	right: 0; \r\n	float: right;\r\n	height:63px;\r\n	width:72px;\r\n	background: transparent url({PXPATH}/lightbox-btn-next.gif)  right 15% no-repeat;\r\n}\r\n#lightbox-container-image-data-box {\r\n	font: 10px Verdana, Helvetica, sans-serif;\r\n	background-color: #fff;\r\n	margin: 0 auto;\r\n	line-height: 1.4em;\r\n	overflow: auto;\r\n	width: 100%;\r\n	padding: 0 10px 0;\r\n}\r\n#lightbox-container-image-data {\r\n	padding: 0 10px; \r\n	color: #666; \r\n}\r\n#lightbox-container-image-data #lightbox-image-details { \r\n	width: 70%; \r\n	float: left; \r\n	text-align: left; \r\n}	\r\n#lightbox-image-details-caption { font-weight: bold; }\r\n#lightbox-image-details-currentNumber {\r\n	display: block; \r\n	clear: left; \r\n	padding-bottom: 1.0em;	\r\n}\r\n#lightbox-loading-link{\r\n	width: 32px; \r\n	height:32px;\r\n	background:url({PXPATH}/lightbox-ico-loading.gif) no-repeat;\r\n	display:-moz-inline-box;\r\n	display:inline-block;\r\n	vertical-align:bottom;\r\n}		\r\n#lightbox-secNav-btnClose {\r\n	width: 66px; \r\n	height:22px;\r\n	float: right;\r\n	padding-bottom: 0.7em;\r\n	background:url({PXPATH}/lightbox-btn-close.gif) no-repeat;\r\n	display:-moz-inline-box;\r\n	display:inline-block;\r\n	vertical-align:bottom;\r\n}\r\n		', 'http://leandrovieira.com/projects/jquery/lightbox/', '', 'jQuery'); 
INSERT INTO `wp_px_plugins` VALUES (7, 'jqShuffle', 'jqShuffle', '\r\n#px *{\r\n	margin:0;\r\n	padding:0;\r\n	background:none;\r\n	border:none;\r\n	list-style:none;\r\n}\r\n		', 'http://benjaminsterling.com/jquery-jqshuffle/', '{"parameters":[{"param":"auto","desc":"true for automated, false for not"},{"param":"random","desc":"true for random, false for sequence"},{"param":"speed","desc":"animation speed"},{"param":"width","desc":"how wide you want the shuffle to animate"},{"param":"timeout","desc":"duration for each slide (eg. 4000 == 4 seconds)"},{"param":"height","desc":"Usually the height of your hieghest image"}]}', 'jQuery'); 
INSERT INTO `wp_px_plugins` VALUES (8, 'jQuery Lightbox Plugin (balupton edition)', 'lightboxbe', '\r\nhtml, body { margin: 0; padding: 0; height: 100%;}\r\n\r\n#lightbox, #lightbox-overlay {\r\n	position: absolute;\r\n	top: 0px;\r\n	left: 0px;\r\n	bottom:auto;\r\n	right:auto;\r\n	\r\n	z-index:100;\r\n	width:100%;\r\n	height:auto;\r\n	\r\n	text-align:center;\r\n	color:#333333;\r\n	\r\n	/* stop stupid conflicts */\r\n	margin:0px;\r\n	padding:0px;\r\n	border:none;\r\n	outline:none;\r\n	line-height:0;\r\n	\r\n	/* general conflict stopper */\r\n	text-decoration:none;\r\n	background:none;\r\n	word-spacing:normal;\r\n	letter-spacing:normal;\r\n	float:none;\r\n	clear:none;\r\n	display:block;\r\n}\r\n\r\n#lightbox-overlay {\r\n	z-index: 90;\r\n	background-color:#000000;\r\n	height: 100%;\r\n}\r\n#lightbox-overlay-text {\r\n	text-align: right;\r\n	margin-right: 20px;\r\n	margin-top: 20px;\r\n	color: white;\r\n	font-size: 12px;\r\n	cursor: default;\r\n	line-height:normal;\r\n}\r\n#lightbox-overlay-text a, #lightbox-overlay-text a:hover, #lightbox-overlay-text a:visited, #lightbox-overlay-text a:link {\r\n	text-decoration:underline;\r\n	color:white;\r\n}\r\n#lightbox-overlay-text span {\r\n	padding-left:5px;\r\n	padding-right:5px;\r\n}\r\n\r\n#lightbox img, #lightbox a img, #lightbox a { border:none; outline:none; }\r\n\r\n#lightbox-imageBox {\r\n	position:relative;\r\n	border:1px solid black;;\r\n	background-color:white;\r\n	width:250px;\r\n	height:250px;\r\n	margin:0 auto;\r\n}\r\n\r\n#lightbox-imageContainer {\r\n	padding:1px;\r\n}\r\n\r\n#lightbox-loading {\r\n	position:absolute;\r\n	top:40%;\r\n	left:0%;\r\n	height:25%;\r\n	width:100%;\r\n	text-align:center;\r\n	line-height:0;\r\n}\r\n\r\n#lightbox-nav {\r\n	position:absolute;\r\n	top:0;\r\n	left:0;\r\n	height:100%;\r\n	width:100%;\r\n	z-index:10;\r\n}\r\n/* #lightbox-imageBox > #lightbox-nav { left: 0; } \r\n#lightbox-nav a { outline: none; }*/\r\n\r\n\r\n#lightbox-nav-btnPrev, #lightbox-nav-btnNext {\r\n	display:block;\r\n	width:49%;\r\n	height: 100%;\r\n	background:transparent url(\\"{PXPATH}/blank.gif\\") no-repeat; /* Trick IE into showing hover */\r\n	/* cursor:pointer; */\r\n	zoom:1; /* who knows why? */\r\n	\r\n	padding:0px;\r\n	margin:0px;\r\n}\r\n#lightbox-nav-btnPrev { \r\n	left:0;\r\n	right:auto;\r\n	float:left;\r\n}\r\n#lightbox-nav-btnNext { \r\n	left:auto;\r\n	right:0;\r\n	float:right;\r\n}\r\n/*\r\n.preload_largeLink, #prevLink:hover, #prevLink:visited:hover {\r\n	background:url(\\"{PXPATH}/prev.gif\\") left 45% no-repeat;\r\n}\r\n.preload_nextLink, #nextLink:hover, #nextLink:visited:hover {\r\n	background:url(\\"{PXPATH}/next.gif\\") right 45% no-repeat;\r\n}\r\n*/\r\n\r\n#lightbox-infoBox {\r\n	font:10px Verdana, Helvetica, sans-serif;\r\n	background-color:#FFFFFF;\r\n	margin:0 auto;\r\n	padding:none;\r\n	\r\n	/* width: 100%;\r\n	padding: 0 10px 0; */\r\n}\r\n\r\n#lightbox-infoContainer {\r\n	padding-left:10px;\r\n	padding-right:10px;\r\n	padding-top:5px;\r\n	padding-bottom:5px;\r\n	color:#666;\r\n	\r\n	line-height:normal;\r\n	/* height:30px; */\r\n}\r\n#lightbox-infoHeader {\r\n	width:100%;\r\n	text-align:center; \r\n}\r\n#lightbox-caption {\r\n	text-align:justify;\r\n}\r\n#lightbox-caption-title {\r\n	font-weight:bold;\r\n}\r\n#lightbox-caption-description {\r\n	font-weight:normal;\r\n}\r\n\r\n#lightbox-infoFooter {\r\n	margin-top:3px;\r\n	color:#999999;\r\n}\r\n#lightbox-currentNumber {\r\n	display:block;\r\n	width:49%;\r\n	float:left;\r\n	text-align:left;\r\n}\r\n#lightbox-close {\r\n	display:block;\r\n	width:45%;\r\n	float:right;\r\n	text-align:right;\r\n}\r\n#lightbox-close-button{\r\n	padding-left:30%;\r\n}\r\n#lightbox-close-button, #lightbox-close-button:link, #lightbox-close-button:visited, #lightbox-close-button:hover {\r\n	text-decoration:underline;\r\n	color:#999999;\r\n}\r\n#lightbox-close-button:hover {\r\n	color:#666666;\r\n}\r\n\r\n#lightbox-infoContainer-clear {\r\n	clear:both; \r\n	visibility:hidden;\r\n}\r\n		', 'http://jquery.com/plugins/project/jquerylightbox_bal', '', 'jQuery');
